const landing = document.getElementById('landing');
const orderBtn = document.getElementById('orderBtn');
const shop = document.getElementById('shop');
const categories = document.querySelectorAll('.category-btn');
const productsDiv = document.getElementById('products');
const cartItemsUl = document.getElementById('cartItems');
const cartTotalDiv = document.getElementById('cartTotal');

let cart = {};

// Show products for a category
function showProducts(category) {
  productsDiv.innerHTML = '';
  const items = productsData[category];
  items.forEach(product => {
    const card = document.createElement('div');
    card.classList.add('product-card');
    card.innerHTML = `
      <img src="${product.image}" alt="${product.name}" />
      <div class="product-name">${product.name}</div>
      <div class="product-price">$${product.price.toFixed(2)}</div>
      <button class="add-btn" data-id="${product.id}" data-cat="${category}">Add to Cart</button>
    `;
    productsDiv.appendChild(card);
  });
}

// Update Cart display
function updateCart() {
  cartItemsUl.innerHTML = '';
  let total = 0;
  for (const key in cart) {
    const { product, qty } = cart[key];
    total += product.price * qty;
    const li = document.createElement('li');
    li.innerHTML = `
      <div class="cart-item-name">${product.name}</div>
      <div class="cart-item-qty">Qty: ${qty}</div>
      <div>Price: $${(product.price * qty).toFixed(2)}</div>
    `;
    cartItemsUl.appendChild(li);
  }
  cartTotalDiv.textContent = `Total: $${total.toFixed(2)}`;
}

// Add to cart handler
productsDiv.addEventListener('click', e => {
  if (e.target.classList.contains('add-btn')) {
    const id = e.target.dataset.id;
    const cat = e.target.dataset.cat;
    const product = productsData[cat].find(p => p.id == id);
    if (cart[id]) {
      cart[id].qty++;
    } else {
      cart[id] = { product, qty: 1 };
    }
    updateCart();
  }
});

// Category buttons handler
categories.forEach(btn => {
  btn.addEventListener('click', () => {
    categories.forEach(b => b.classList.remove('active'));
    btn.classList.add('active');
    showProducts(btn.dataset.cat);
  });
});

// Order button click - show shop & hide landing
orderBtn.addEventListener('click', () => {
  landing.classList.add('hidden');
  shop.classList.remove('hidden');
  showProducts('men'); // default to men category on open
});
document.addEventListener("DOMContentLoaded", () => {
  const checkoutBtn = document.getElementById("checkoutBtn");
  checkoutBtn.textContent = "Checkout";
  checkoutBtn.id = "checkoutBtn";
  document.querySelector("#cart").appendChild(checkoutBtn);

  const addressModal = document.getElementById("addressModal");
  const addressForm = document.getElementById("addressForm");
  const paymentSection = document.getElementById("paymentSection");

  checkoutBtn.addEventListener("click", () => {
    addressModal.style.display = "flex";
  });

  addressForm.addEventListener("submit", (e) => {
    e.preventDefault();
    addressModal.style.display = "none";
    paymentSection.style.display = "flex";
  });
});